#include <iostream>
#include <sstream>
#include <fstream>

using namespace std;

void export_answer(stringstream &);


int main()
{
    stringstream s;
    string number = "";
    int window = 13;
    unsigned long long sol = 0;
    for(int i = 0; i < 1001 - window; i++)
    {
        string sstr = number.substr(i, window);
        unsigned long long temp_mul = 1;
        for (int j = 0; j < window; j++)
        {
            temp_mul *= static_cast<int>(sstr[j]) - 48;
        }

        if (temp_mul >= sol) {sol = temp_mul;}
    }
    s << sol;
    export_answer(s);

    return 0;
}


void export_answer(stringstream &sstream)
{
    fstream f;
    f.open("solution.txt", ios::out);
    f << sstream.str();
    f.close();
}
